mapper.select:
SELECT (SELECT STUFF((SELECT ','+ltrim(cast( CASE WHEN pay_mode =1 THEN '支付宝' WHEN pay_mode =2 THEN '刷卡' WHEN pay_mode =3 THEN '微信' WHEN pay_mode =4 THEN '现金' WHEN pay_mode =5 THEN '转账' WHEN pay_mode =6 THEN '花呗' WHEN pay_mode =7 THEN '其他' ELSE '未设置' END as varchar(10))+':'+cast(pay_price as varchar(10))) FROM fr_card_order_pay_mode WHERE order_id = a.split_set_id and (order_type ='1' or order_type = '2') FOR XML PATH('')), 1, 1, '') AS keyword FROM fr_card_order_pay_mode t WHERE t.order_id = a.split_set_id GROUP BY t.order_id, t.order_type ) payMoney, (SELECT STUFF((SELECT ','+ltrim(cast( CASE WHEN pay_mode =1 THEN '支付宝' WHEN pay_mode =2 THEN '刷卡' WHEN pay_mode =3 THEN '微信' WHEN pay_mode =4 THEN '现金' WHEN pay_mode =5 THEN '转账' WHEN pay_mode =6 THEN '花呗' WHEN pay_mode =7 THEN '其他' ELSE '未设置' END as varchar(10))+':'+cast(pay_price as varchar(10))) FROM fr_card_order_pay_mode WHERE order_id = a.id and order_type ='6' FOR XML PATH('')), 1, 1, '') AS keyword FROM fr_card_order_pay_mode t WHERE t.order_id = a.id GROUP BY t.order_id, t.order_type ) paymentMoney, (SELECT s.ShopName FROM Shop s WHERE a.shop_id = s.id) shopName, (SELECT p.RelName FROM PersonnelInfo p WHERE a.personnel_id = p.ID) relName, (SELECT c.allot_type FROM fr_card_order_allot_set c WHERE a.id= c.order_id ) allotType, (SELECT c.allot_num FROM fr_card_order_allot_set c WHERE a.id= c.order_id ) allotNum,
FROM fr_card_order_complement a WHERE a.is_using = '1' AND a.CustomerCode = #{CustomerCode} AND a.card_id = #{cardId} AND a.client_id = #{clientId}
AND a.order_state = #{orderState}
AND a.status = #{status}
AND a.audit_status = #{auditStatus}
AND a.is_using = '1'
