SELECT , (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 1 and eci.is_use = 1) eduCurrentCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 0 and eci.is_use = 1) eduCancelCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 2 and eci.is_use = 1) eduToConfirmCount, ec.reserve_time configReserveTime, ec.setting_time configSettingTime, ec.is_start_class configStartClass, ec.is_choose_coach configChooseCoach, ec.is_reserve_confirm configReserveConfirm, ec.admission_time configAdmissionTime, ec.is_online_reserve configOnlineReserve, ec.is_card_settle configCardSettle, ec.is_course_settle configCourseSettle, gc.image_url courseImage, gc.member_price courseMemberPrice, ec.id configId FROM fr_education edu left join fr_education_config ec on edu.id = ec.education_id left join fr_group_course gc on gc.id = edu.course_id WHERE edu.shop_id = #{shopId}
AND edu.coach_id = #{coachId}
AND edu.CustomerCode = #{CustomerCode}
AND edu.sdaduim_id = #{sdaduimId}
AND edu.execute_date_plan >= #{beginDate}
AND edu.execute_date_plan <= #{endDate}
AND edu.type = ${eduType} ORDER BY edu.execute_date_plan ASC

SELECT , (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 1 and eci.is_use = 1) eduCurrentCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 0 and eci.is_use = 1) eduCancelCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 2 and eci.is_use = 1) eduToConfirmCount, ec.reserve_time configReserveTime, ec.setting_time configSettingTime, ec.is_start_class configStartClass, ec.is_choose_coach configChooseCoach, ec.is_reserve_confirm configReserveConfirm, ec.admission_time configAdmissionTime, ec.is_online_reserve configOnlineReserve, gc.private_image courseImage, gc.member_price courseMemberPrice, ec.id configId FROM fr_education edu left join fr_education_config ec on edu.id = ec.education_id left join fr_private_cource pc on pc.id = edu.course_id WHERE edu.shop_id = #{shopId}
AND edu.coach_id = #{coachId}
AND edu.CustomerCode = #{CustomerCode}
AND edu.sdaduim_id = #{sdaduimId}
AND edu.execute_date_plan >= #{beginDate}
AND edu.execute_date_plan <= #{endDate}
AND edu.type = ${eduType} and edu.reserve_type = 2 ORDER BY edu.execute_date_plan ASC

SELECT , (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 1 and eci.is_use = 1) eduCurrentCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 0 and eci.is_use = 1) eduCancelCount, (select count(1) from fr_education_client_info eci where eci.education_id = edu.id and eci.reserve_status = 2 and eci.is_use = 1) eduToConfirmCount, eci.member_id infoMemberId, eci.member_name infoMemberName, eci.mobile infoMobile, eci.reserve_client_name infoReserveClientName, eci.reserve_client_id infoReserveClientId, eci.reserve_date infoReserveDate, eci.settle_type infoSettleType, eci.deduction_balance infoDeductionBalance, eci.reserve_status infoReserveStatus, eci.remarks infoRemarks, gc.private_image courseImage, gc.member_price courseMemberPrice FROM fr_education edu left join fr_education_client_info eci on edu.id = eci.education_id left join fr_private_cource pc on pc.id = edu.course_id WHERE edu.shop_id = #{shopId}
AND edu.coach_id = #{coachId}
AND edu.CustomerCode = #{CustomerCode}
AND edu.sdaduim_id = #{sdaduimId}
AND edu.execute_date_plan >= #{beginDate}
AND edu.execute_date_plan <= #{endDate}
AND edu.type = ${eduType} and edu.reserve_type = 1 ORDER BY edu.execute_date_plan ASC

SELECT c.id clientId, c.client_name clientName, ( CASE WHEN c.sex = 1 THEN '女' ELSE '男' END ) sex, card.card_no cardNo, ( SELECT a.card_type_name FROM fr_card_type a WHERE a.is_using = 1 AND a.CustomerCode = #{CustomerCode} AND a.id = ( SELECT b.card_type_id FROM fr_card b WHERE b.id = card.id )) cardTypeName, card.bind_time bindDate, card.invalid_time invalidDate, card.have_rights_num haveRightsNum, ( SELECT pi.RelName FROM PersonnelInfo pi LEFT JOIN fr_card_order_info coi ON coi.personnel_id = pi.ID WHERE coi.card_id = card.id AND coi.client_id = c.id ) salesName, ( SELECT TOP 1 order_amt FROM fr_card_order_datail WHERE card_id = card.id AND client_id = c.id AND type = 1 AND is_using = 1 ORDER BY create_time DESC ) totalAmt, c.consultant_name consultantName, /* 服务顾问*/ card.status status, ( SELECT ( CASE
WHEN a.type = 1 THEN '时间卡' WHEN a.type = 2 THEN '小时卡' WHEN a.type = 3 THEN '次卡' WHEN a.type = 4 THEN '储值卡' WHEN a.type = 5 THEN '充值卡' WHEN a.type = 6 THEN '折扣卡' WHEN a.type = 7 THEN '员工卡' END ) FROM fr_card_type a WHERE a.is_using = 1 AND a.CustomerCode = #{CustomerCode} AND a.id = ( SELECT b.card_type_id FROM fr_card b WHERE b.id = card.id )) cardType, ( SELECT pic_link FROM fr_client_pic WHERE client_id = c.id AND pic_type = 1 ) clientPic, ( SELECT ( CASE WHEN ( SELECT id FROM fr_client_personal cp WHERE cp.client_id = cli.id ) IS NULL THEN ( CASE WHEN ( SELECT id FROM fr_client_latence_personal clp WHERE clp.client_id = cli.id ) IS NULL THEN NULL ELSE '潜在客户' END ) ELSE '现有客户' END ) memberType FROM fr_client cli WHERE cli.is_using = 1 and cli.id = c.id ) memberType, c.mobile, card.id cardId, card.card_type_id cardTypeId FROM fr_client c LEFT JOIN fr_card card ON card.client_id = c.id /* LEFT JOIN fr_card_type ct ON ct.id = card.card_type_id*/ WHERE
c.mobile = #{searchInput}
card.card_no = #{searchInput}
AND c.is_using = 1 AND c.CustomerCode = #{CustomerCode} AND card.is_using = 1 and card.shop_id = #{shopId} and card.status = 0 order by card.invalid_time asc

SELECT eci.member_id clientId, eci.member_name clientName, eci.sex, card.card_no cardNo, ( SELECT a.card_type_name FROM fr_card_type a WHERE a.is_using = 1 AND a.CustomerCode = #{CustomerCode} AND a.id = ( SELECT b.card_type_id FROM fr_card b WHERE b.id = card.id )) cardTypeName, card.bind_time bindDate, card.invalid_time invalidDate, card.have_rights_num haveRightsNum, ( SELECT pi.RelName FROM PersonnelInfo pi LEFT JOIN fr_card_order_info coi ON coi.personnel_id = pi.ID WHERE coi.card_id = card.id AND coi.client_id = eci.member_id ) salesName, ( SELECT TOP 1 order_amt FROM fr_card_order_datail WHERE card_id = card.id AND client_id = eci.member_id AND type = 1 AND is_using = 1 ORDER BY create_time DESC ) totalAmt, cl.consultant_name consultantName, /* 服务顾问*/ card.status status, ( SELECT ( CASE
WHEN a.type = 1 THEN '时间卡' WHEN a.type = 2 THEN '小时卡' WHEN a.type = 3 THEN '次卡' WHEN a.type = 4 THEN '储值卡' WHEN a.type = 5 THEN '充值卡' WHEN a.type = 6 THEN '折扣卡' WHEN a.type = 7 THEN '员工卡' END ) FROM fr_card_type a WHERE a.is_using = 1 AND a.CustomerCode = #{CustomerCode} AND a.id = ( SELECT b.card_type_id FROM fr_card b WHERE b.id = card.id )) cardType, ( SELECT pic_link FROM fr_client_pic WHERE client_id = eci.member_id AND pic_type = 1 ) clientPic, ( SELECT( CASE WHEN ( SELECT id FROM fr_client_personal cp WHERE cp.client_id = cli.id ) IS NULL THEN ( CASE WHEN ( SELECT id FROM fr_client_latence_personal clp WHERE clp.client_id = cli.id ) IS NULL THEN NULL ELSE '潜在客户' END ) ELSE '现有客户' END ) memberType FROM fr_client cli WHERE cli.is_using = 1 and cli.id = cl.id ) memberType, cl.mobile, card.id cardId, card.card_type_id cardTypeId FROM fr_education_client_info eci LEFT JOIN fr_card card ON card.client_id = eci.member_id LEFT JOIN fr_client cl ON cl.id = eci.member_id LEFT JOIN fr_education ed ON eci.education_id = ed.id WHERE
eci.mobile = #{searchInput}
card.card_no = #{searchInput}
AND eci.is_use = 1 AND eci.reserve_status = 1 AND ed.CustomerCode = #{CustomerCode} AND card.is_using = 1 AND ed.shop_id = #{shopId} and card.status = 0 order by card.invalid_time asc
