mapper.select:
select

FROM  PersonnelInfo pi
LEFT JOIN   PersonlRole pr
ON  pi.ID = pr.PersonnelInfoId
LEFT JOIN   RoleInfo r
ON  pr.RoleInfoId = r.id
WHERE r.UserType = #{UserType}
AND pi.ShopId = #{shopId}
AND pi.CustomerCode = #{CustomerCode}

SELECT r.id rId, r.FirstName, r.ParentId, r.RoleType, r.ISTeach, r.ServiceRole, r.BusinessType, r.UserType, rf.RoleInfoId, p.RelName, p.id pId, p.ShopId FROM PersonnelInfo p LEFT JOIN PersonlRole rf ON rf.PersonnelInfoId = p.id LEFT JOIN RoleInfo r ON r.id = rf.RoleInfoId WHERE r.IsDel=0 AND rf.IsDel = 0 AND p.IsDel = 0 AND p.Status = 0 AND p.CustomerCode = #{CustomerCode} AND p.ShopId = #{shopId}
AND (
r.BusinessType = #{BusinessType}
)
